package com.example.sneakers.db.dao

import androidx.room.*
import com.example.sneakers.models.Sneaker
import kotlinx.coroutines.flow.Flow

@Dao
interface SneakerDao {

    @Query("SELECT * FROM sneakers")
    fun getAllSneakers(): Flow<List<Sneaker>>

    @Query("SELECT * FROM sneakers WHERE id = :sneakerId")
    fun getSneakerById(sneakerId: String): Flow<Sneaker>

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertSneaker(sneaker: Sneaker)

    @Delete
    suspend fun deleteSneaker(sneaker: Sneaker)

    @Query("DELETE FROM sneakers")
    suspend fun deleteAllSneakers()

    @Query("SELECT * FROM sneakers WHERE cartAdded = 1")
    fun getAllCartAddedSneakers(): Flow<List<Sneaker>>

